BEGIN TRANSACTION;\n\nIF EXISTS (\n    SELECT 1 \n    FROM sys.all_columns \n    WHERE object_id = OBJECT_ID(N'[dbo].[MyTable]')\n    AND name = 'Column1'\n)\nBEGIN\n    -- Update data in a transaction-safe manner.\n    UPDATE [dbo].[MyTable] \n    SET [Column2] = [Column1];\n\n    --- Drop the column only after a successful update.\n    ALTER TABLE [dbo].[MyTable]\n    DROP COLUMN [Column1];\nEND\n\nCOMMIT TRANSACTION;