-- Step 1: Create a new login if necessary\nUSE master;\nGO\n\nIF NOT EXISTS (SELECT * FROM sys.server_principals WHERE name = 'domain\abc')\n    CREATE LOGIN [domain\abc] FROM WINDOWS;\n\n-- Step 2: Change the database owner to sa or another trusted account temporarily\nUSE YourDatabaseName;\nGO\n\nEXEC sp_changedbowner 'sa';\n\n-- Step 3: Drop and recreate the dbo user associated with new login\nUSE master;\nGO\n\nDROP USER [YourDatabaseName].[dbo];\nCREATE USER [dbo] FOR LOGIN [domain\abc];\n\n-- Step 4: Set the database owner back to the new dbo if needed or use another trusted account\nUSE YourDatabaseName;\nGO\n\nEXEC sp_changedbowner 'domain\abc';