-- Assuming we have a Products table with columns: ProductID, Name, Price, Discount.\nCREATE TABLE Products (\n    ProductID INT PRIMARY KEY,\n    Name NVARCHAR(100),\n    Price DECIMAL(18, 2),\n    Discount DECIMAL(4, 2)\n);\n\n-- Create a wrapper view for the Products table\nCREATE VIEW vProducts AS\nSELECT \n    ProductID,\n    Name,\n    Price,\n    Discount,\n    (Price * (1 - (Discount / 100))) AS SalePrice  -- Computed column based on existing data\nFROM \n    Products;