SELECT \n    c.ContractName,\n    s.ServiceName,\n    (SUM(sta.CompletionPercentage) / COUNT(sta.StandardId)) * 100 AS "Percent Complete"\nFROM \n    dbo.Contracts c\nINNER JOIN \n    dbo.Contract_Service conSer ON c.ContractID = conSer.ContractID\nINNER JOIN \n    dbo.Services s ON conSer.ServiceId = s.ServiceId\nINNER JOIN \n    dbo.Standard sta ON s.ServiceId = sta.ServiceId\nLEFT OUTER JOIN \n    dbo.StandardResponse standResp ON sta.StandardId = standResp.StandardId\n        AND conSer.StandardReportId = standResp.StandardReportId\nGROUP BY \n    c.ContractName, \n    s.ServiceName;