-- Assume we have a table named 'ExampleTable' with an identity column\nCREATE TABLE ExampleTable (\n    Id INT IDENTITY(1,1) PRIMARY KEY,\n    Data VARCHAR(50)\n);\n\n-- Stored procedure to insert data into the table\nCREATE PROCEDURE SPInsertData @data VARCHAR(50)\nAS\nBEGIN\n    INSERT INTO ExampleTable (Data) VALUES (@data);\nEND;\n\n-- Executing the stored procedure multiple times\nEXEC SPInsertData 'First Record';\nEXEC SPInsertData 'Second Record';\nEXEC SPInsertData 'Third Record';\n\n-- Selecting data to show the auto-incremented identity column values\nSELECT * FROM ExampleTable;