-- Using NUMBER(1) with CHECK constraint for BOOLEAN simulation\nCREATE TABLE example_table (\n    id NUMBER,\n    is_active NUMBER(1) CHECK (is_active IN (0, 1)),\n    CONSTRAINT pk_example PRIMARY KEY (id)\n);\n\n-- Using CHAR(1) with CHECK constraint for BOOLEAN simulation\nCREATE TABLE example_table_char (\n    id NUMBER,\n    is_active CHAR(1) CHECK (is_active IN ('Y', 'N')),\n    CONSTRAINT pk_example_char PRIMARY KEY (id)\n);