CREATE TABLE PERSON (\n    name VARCHAR(255) PRIMARY KEY,\n    -- other attributes if necessary\n);\n\nCREATE TABLE RELATIONSHIP (\n    id SERIAL PRIMARY KEY,\n    person1_name VARCHAR(255),\n    person2_name VARCHAR(255),\n    UNIQUE (person1_name),  -- Ensures each person can only have one relationship\n    UNIQUE (person2_name),  -- Ensures reciprocal uniqueness, i.e., no two entries can have the same person as spouse\n    FOREIGN KEY (person1_name) REFERENCES PERSON(name),\n    FOREIGN KEY (person2_name) REFERENCES PERSON(name)\n);