CREATE PROCEDURE AccountTable_Update\n    @Id          INT,\n    @FirstName  NVARCHAR(50), -- Matched with the table definition\n    @LastName   NVARCHAR(50),\n    @StreetName NVARCHAR(50),\n    @StateId  INT\nAS\nBEGIN\n    BEGIN TRY\n        UPDATE AccountTable \n        SET FirstName = @FirstName,\n            LastName = @LastName,\n            Street = @StreetName,\n            StateId = @StateId \n        WHERE AccountId = @Id;\n        \n        IF @@ROWCOUNT = 0 -- Check if any row was affected\n            PRINT 'No rows were updated. Please check the AccountId.';\n    END TRY\n    BEGIN CATCH\n        PRINT ERROR_MESSAGE(); -- Basic error handling\n    END CATCH\nEND