DECLARE v_sql VARCHAR(1024);\nDECLARE v_table_name VARCHAR(50) DEFAULT 'MYTABLE';\nDECLARE v_id_column VARCHAR(50) DEFAULT 'ID';\nDECLARE v_new_id CHAR(10) DEFAULT 'NEW_ID'; -- Adjust as necessary for your data type and value\n\n-- Construct the SELECT part of the SQL\nSELECT LISTAGG('T.' || COLUMN_NAME, ', ') WITHIN GROUP (ORDER BY ORDINAL_POSITION)\nINTO v_sql\nFROM SYSCAT.COLUMNS\nWHERE TABNAME = v_table_name AND COLUMN_NAME <> v_id_column;\n\n-- Construct and execute the full INSERT ... SELECT statement\nSET v_sql = 'INSERT INTO ' || v_table_name || '(' ||\n            REPLACE(SUBSTR(v_sql, 1, LENGTH(v_sql) - 2), 'T.', '') || ', ID)' || -- Remove trailing comma and replace 'T.' prefix with nothing\n            ' SELECT ' || v_sql || ', ''' || v_new_id || '''' ||\n            ' FROM ' || v_table_name || ' T';\n\nEXECUTE IMMEDIATE v_sql;