SELECT *\nFROM (\n    SELECT \n        c.CustomerID,\n        od.CategoryID,\n        COUNT(*) as OrderCount\n    FROM \n        Customers c\n    JOIN \n        Orders o ON c.CustomerID = o.CustomerID\n    JOIN \n        `Order Details` od ON o.OrderID = od.OrderID\n    GROUP BY \n        c.CustomerID, od.CategoryID\n)\nPIVOT (\n    SUM(OrderCount) FOR CategoryID IN (\n        -- List all category IDs here. Use actual category numbers from your table.\n        1 AS Beverages,\n        2 AS Condiments,\n        3 AS Confections,\n        4 AS DairyProducts,\n        5 AS GrainsCereals,\n        6 AS MeatPoultry,\n        7 AS Produce\n    )\n)\nORDER BY CustomerID;