-- Assume we have a primary table called Bills and a mirror table called Bills_Audit\n\nCREATE TRIGGER trg_Bills_Update ON Bills\nAFTER UPDATE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    INSERT INTO Bills_Audit (BillId, Description, Amount, LastUpdated, Action)\n    SELECT I.BillId, I.Description, I.Amount, GETDATE(), 'UPDATE'\n    FROM inserted I;\nEND\n\nCREATE TRIGGER trg_Bills_Insert ON Bills\nAFTER INSERT\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    INSERT INTO Bills_Audit (BillId, Description, Amount, LastUpdated, Action)\n    SELECT I.BillId, I.Description, I.Amount, GETDATE(), 'INSERT'\n    FROM inserted I;\nEND\n\nCREATE TRIGGER trg_Bills_Delete ON Bills\nAFTER DELETE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    INSERT INTO Bills_Audit (BillId, Description, Amount, LastUpdated, Action)\n    SELECT D.BillId, D.Description, D.Amount, GETDATE(), 'DELETE'\n    FROM deleted D;\nEND