CREATE TRIGGER update_row_numbers_after_delete\nON your_table_name\nAFTER DELETE\nAS\nBEGIN\n    -- Delete operation in a trigger will leave you with the DELETED table which has information about what was just deleted.\n    DECLARE @deleted_row_number INT\n    \n    SELECT @deleted_row_number = row_number FROM DELETED\n\n    UPDATE your_table_name\n    SET row_number = row_number - 1\n    WHERE row_number > @deleted_row_number\nEND