-- Create sample data:\nCREATE TEMPORARY TABLE letters (letter CHAR(1));\nINSERT INTO letters VALUES ('A'), ('B'), ('C');\n\nCREATE TEMPORARY TABLE numbers (number INT);\nINSERT INTO numbers VALUES (1), (2), (3);\n\nCREATE TEMPORARY TABLE mixed (letter CHAR(1), number INT);\nINSERT INTO mixed VALUES ('A', 1), ('A', 2), ('B', 2), ('B', 3), ('C', 1), ('C', 3);\n\n-- Find missing entries\nSELECT letter, number\nFROM (\n    SELECT l.letter, n.number \n    FROM letters l\n    CROSS JOIN numbers n\n) AS all_combinations\nLEFT JOIN mixed m ON all_combinations.letter = m.letter AND all_combinations.number = m.number\nWHERE m.letter IS NULL OR m.number IS NULL;