IF EXISTS (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Posted_Flag = 1\n    AND Staff_Id = @PersonID\n)\nBEGIN\n    RAISERROR('Timesheets have already been posted!', 16, 1);\n    ROLLBACK TRAN;\nEND\nELSE IF NOT EXISTS (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Staff_Id = @PersonID\n)\nBEGIN\n    RAISERROR('Default list has not been loaded!', 16, 1);\n    -- Optionally rollback if you want to stop the trigger action\n    -- ROLLBACK TRAN;\nEND\nELSE\nBEGIN\n    -- Proceed with updating Timesheet_Hours\n    -- Your update logic here\n    PRINT 'Proceeding with update'; -- Placeholder for your actual logic\nEND