-- Find foreign key constraints referencing the column\n   SELECT \n       fk.name AS ForeignKeyConstraintName,\n       OBJECT_NAME(fk.parent_object_id) AS TableName\n   FROM sys.foreign_key_columns fk\n   WHERE fk.referenced_object_id = OBJECT_ID('MyTable')\n     AND fk.referenced_column_id = COLUMNPROPERTY(OBJECT_ID('MyTable'), 'MyField', 'ColumnId');\n\n   -- Find other constraints (e.g., checks, defaults) on the column\n   SELECT \n       name AS ConstraintName,\n       type_desc AS ConstraintType\n   FROM sys.objects\n   WHERE parent_object_id = OBJECT_ID('MyTable')\n     AND parent_column_id = COLUMNPROPERTY(OBJECT_ID('MyTable'), 'MyField', 'ColumnId');