CREATE TABLE users (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(96), -- Reasonable length for names that avoids wasteful space\n    email VARCHAR(255) UNIQUE, -- Standard length for emails\n    is_active BOOLEAN DEFAULT TRUE,\n    preferences SET('news', 'offers'), -- Efficient storage for a small set of options\n    created_at DATETIME DEFAULT CURRENT_TIMESTAMP\n);