SET ANSI_NULLS ON\nGO\nSET QUOTED_IDENTIFIER ON\nGO\n\nALTER VIEW [dbo].[viewProductExportUpdated2]\nAS\nSELECT     dbo.ProductCategories.ProductCategoryID AS ProductCode, \n           dbo.ProductCategories.ProductCategoryName AS ProductTitle, \n           dbo.Products.ShortDescription AS ProductShortDesc, \n           dbo.Products.LongDescription AS ProductLongDesc,\n           CAST(dbo.Products.ProductXML AS XML) AS ProdXml, \n           (CAST(dbo.Products.ProductXML AS XML)).value('data(Products/PackSize)[1]', 'nvarchar(max)') AS ProductPackSize  \nFROM         dbo.ProductCategories \nINNER JOIN dbo.Products ON dbo.ProductCategories.ProductCategoryID = dbo.Products.ProductCategoryID\nWHERE     (dbo.ProductCategories.Deleted = 0)\nGO\n\nSET ANSI_NULLS OFF\nGO\nSET QUOTED_IDENTIFIER OFF\nGO