-- Assume we have an original table named 'documents'\nCREATE TABLE documents (\n    id INT PRIMARY KEY,\n    name VARCHAR(255),\n    content TEXT,\n    last_modified TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\n-- Insert some sample data\nINSERT INTO documents (id, name, content) VALUES\n(1, 'Document A', 'Content of Document A'),\n(2, 'Document B', 'Content of Document B');\n\n-- Create a versioned copy of the table named 'documents_version_1'\nCREATE TABLE documents_version_1 AS SELECT * FROM documents WITH NO DATA;\n\n-- Insert data from original table to the versioned table\nINSERT INTO documents_version_1 (id, name, content, last_modified)\nSELECT id, name, content, last_modified FROM documents;\n\n-- Verify data in versioned copy\nSELECT * FROM documents_version_1;