-- Change these variables accordingly\nDECLARE @Publisher AS sysname = 'YourPublisher';\nDECLARE @PublisherDB AS sysname = 'YourPublisherDB';\nDECLARE @Publication AS sysname = 'YourPublicationName';\nDECLARE @Subscriber AS sysname = 'YourSubscriber';\nDECLARE @AlternateSnapshotFolder AS nvarchar(256) = N'\\Path\To\Snapshot\Files';\n\n-- Add the subscriber with a new subscription\nEXEC sp_addmergesubscription \n    @publication = @Publication,\n    @subscriber = @Subscriber,\n    @subscriber_db = 'YourSubscriberDB',\n    @subscription_type = N'Push',\n    @sync_type = N'automatic',\n    @description = N'Reinitialized Subscriber';\n\n-- Update the subscription to use the alternate snapshot location\nEXEC sp_changemergesubscription \n    @publication = @Publication, \n    @subscriber = @Subscriber,\n    @property = N'snapshot_folder', \n    @value = @AlternateSnapshotFolder;\n\n-- Start synchronizing the new subscription manually if needed\nEXEC sp_startpublication_snapshot @publication = @Publication;