SELECT \n    CASE \n        WHEN EXISTS (\n            SELECT 1\n            FROM date_ranges_table drt\n            WHERE (drt.start_date <= '18/06/2023' AND drt.end_date >= '06/06/2023')\n               OR (drt.start_date BETWEEN '06/06/2023' AND '18/06/2023')\n               OR (drt.end_date BETWEEN '06/06/2023' AND '18/06/2023')\n        ) THEN 'IN LIST'\n        ELSE 'NOT IN LIST'\n    END AS result;