WITH RecursiveCTE AS (\n    -- Anchor member: Select the initial children of the specified parent\n    SELECT NodeId, ParentId, NodeName\n    FROM YourTableName\n    WHERE ParentId = 1\n\n    UNION ALL\n    \n    -- Recursive member: Keep selecting children until there are none left\n    SELECT t.NodeId, t.ParentId, t.NodeName\n    FROM YourTableName t\n    INNER JOIN RecursiveCTE cte ON t.ParentId = cte.NodeId\n)\nSELECT NodeId, ParentId, NodeName\nFROM RecursiveCTE;