SELECT \n    c.table_name,\n    c.column_name,\n    c.is_nullable,\n    COALESCE(ut.name, t.name) AS data_type,\n    CASE WHEN ut.user_type_id IS NOT NULL THEN st.name ELSE NULL END AS user_defined_type_schema\nFROM \n    MY_DB.INFORMATION_SCHEMA.COLUMNS c\nJOIN \n    sys.columns sc ON c.table_name = OBJECT_NAME(sc.object_id) AND c.column_name = sc.name\nLEFT JOIN \n    sys.types t ON sc.system_type_id = t.user_type_id AND t.is_user_defined = 0\nLEFT JOIN \n    sys.types ut ON sc.user_type_id = ut.user_type_id AND ut.is_user_defined = 1\nLEFT JOIN \n    sys.schemas st ON ut.schema_id = st.schema_id\nWHERE \n    c.COLUMN_NAME = 'OrderId'\nORDER BY \n    c.TABLE_NAME;