SELECT \n    p.profile_option_name AS ProfileOption,\n    x.user_profile_option_name AS UserVisibleName,\n    'Level: ' || l.level_value AS Level,\n    CASE l.level_type\n        WHEN 'A' THEN 'Application'\n        WHEN 'U' THEN 'User'\n        WHEN 'R' THEN 'Responsibility'\n        WHEN 'O' THEN 'Organization'\n        WHEN 'S' THEN 'Server'\n        WHEN 'H' THEN 'Host'\n        ELSE l.level_type\n    END AS LevelType,\n    DECODE(l.level_id, 0, '-',\n           CASE l.level_type\n               WHEN 'A' THEN (SELECT application_name FROM fnd_application WHERE application_id = l.level_value)\n               WHEN 'U' THEN (SELECT user_name FROM fnd_user WHERE user_id = l.level_value)\n               WHEN 'R' THEN (SELECT responsibility_name FROM fnd_responsibility_tl WHERE responsibility_id = l.level_value)\n               WHEN 'O' THEN (SELECT organization_name FROM hr_operating_units WHERE organization_id = l.level_value)\n               ELSE l.level_id || '-' || l.level_value\n           END) AS LevelEntity,\n    nvl(Decode(p.site_enabled_flag, 'Y', 'Site:', '') \n        || Decode(p.app_enabled_flag, 'Y', ' Application:', '')\n        || Decode(p.resp_enabled_flag, 'Y', ' Responsibility:', '')\n        || Decode(p.user_enabled_flag, 'Y', ' User:', ''), '-') AS OptionEnabledAt,\n    v.profile_option_value AS Value\nFROM \n    fnd_profile_options p\nJOIN \n    fnd_profile_option_values l ON p.profile_option_id = l.profile_option_id\nJOIN \n    fnd_profile_options_tl x ON p.profile_option_id = x.profile_option_id AND x.language = USERENV('LANG')\nLEFT OUTER JOIN \n    fnd_profile_option_values v ON p.profile_option_id = v.profile_option_id AND l.level_value = v.level_value AND l.level_type = v.level_type\nORDER BY \n    LevelEntity, ProfileOption;