CREATE TABLE Addresses (\n    AddressID INT PRIMARY KEY AUTO_INCREMENT,\n    Street VARCHAR(255) NOT NULL,\n    PostCode VARCHAR(20),\n    City VARCHAR(100),\n    Country VARCHAR(100),\n    UNIQUE (Street, PostCode, City, Country)\n);\n\nCREATE TABLE Suppliers (\n    SupplierID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255) NOT NULL,\n    CurrentAddressID INT,\n    FOREIGN KEY (CurrentAddressID) REFERENCES Addresses(AddressID)\n);\n\nCREATE TABLE PurchaseOrders (\n    OrderID INT PRIMARY KEY AUTO_INCREMENT,\n    SupplierID INT,\n    DeliveryAddressID INT,\n    BillingAddressID INT,\n    ActualDeliveryStreet VARCHAR(255),\n    ActualDeliveryPostCode VARCHAR(20),\n    ActualDeliveryCity VARCHAR(100),\n    ActualDeliveryCountry VARCHAR(100),\n    ActualBillingStreet VARCHAR(255),\n    ActualBillingPostCode VARCHAR(20),\n    ActualBillingCity VARCHAR(100),\n    ActualBillingCountry VARCHAR(100),\n    FOREIGN KEY (SupplierID) REFERENCES Suppliers(SupplierID),\n    FOREIGN KEY (DeliveryAddressID) REFERENCES Addresses(AddressID),\n    FOREIGN KEY (BillingAddressID) REFERENCES Addresses(AddressID)\n);