DECLARE\n  reginfo DBMS_CHANGE_NOTIFICATION.REGISTER_INFO;\nBEGIN\n  DBMS_CHANGE_NOTIFICATION.REGISTER(\n    query     => 'SELECT * FROM my_table',\n    callback  => 'my_change_callback',\n    register_id => reginfo.regid,\n    registration_options => DBMS_CHANGE_NOTIFICATION.OPTION_INSERT +\n                            DBMS_CHANGE_NOTIFICATION.OPTION_UPDATE +\n                            DBMS_CHANGE_NOTIFICATION.OPTION_DELETE\n  );\n  DBMS_OUTPUT.PUT_LINE('Registration ID: ' || TO_CHAR(reginfo.regid));\nEND;\n/