DECLARE\n    v_year        NUMBER := EXTRACT(YEAR FROM SYSDATE);\n    v_start_d     DATE;\n    v_end_d       DATE;\nBEGIN\n    FOR i IN 1..365 LOOP\n        -- Check if the time zone offset changes on each day of the year.\n        IF TO_CHAR( \n                (FROM_TZ(\n                    TO_TIMESTAMP(TO_CHAR(v_year || '-' || LPAD(i,3,'0'),'YYYY-DDD'),'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'YOUR_TIME_ZONE'\n                 ) \n             , 'TZH:TZM') \n            != TO_CHAR( \n                (FROM_TZ(\n                    TO_TIMESTAMP(TO_CHAR(v_year || '-' || LPAD(i-1,3,'0'),'YYYY-DDD'),'YYYY-MM-DD HH24:MI:SS') AT TIME ZONE 'YOUR_TIME_ZONE'\n                 ) \n             , 'TZH:TZM')\n            THEN\n                IF v_start_d IS NULL THEN\n                    v_start_d := TO_TIMESTAMP(TO_CHAR(v_year || '-' || LPAD(i,3,'0'),'YYYY-DDD'),'YYYY-MM-DD HH24:MI:SS');\n                ELSE\n                    v_end_d   := TO_TIMESTAMP(TO_CHAR(v_year || '-' || LPAD(i-1,3,'0'),'YYYY-DDD'),'YYYY-MM-DD HH24:MI:SS');\n                    EXIT;\n                END IF;\n        END IF;\n    END LOOP;\n\n    DBMS_OUTPUT.PUT_LINE('Daylight Saving Time Begins: ' || v_start_d);\n    DBMS_OUTPUT.PUT_LINE('Daylight Saving Time Ends: '   || v_end_d);\nEND;\n/