-- Select the data into grabname as before\nSELECT DISTINCT reqhead_rec.resp_name\nINTO TEMP grabname WITH NO LOG\nFROM reqhead_rec\nJOIN biglist ON reqhead_rec.req_no = biglist.req_no AND reqhead_rec.frm = biglist.req_frm;\n\n-- Use LEFT JOIN to combine biglist and grabname\nSELECT \n    biglist.*,\n    CASE WHEN grabname.resp_name IS NULL THEN '' ELSE grabname.resp_name END AS resp_name\nINTO TEMP xxx WITH NO LOG\nFROM \n    biglist\nLEFT JOIN \n    grabname ON reqhead_rec.req_no = biglist.req_no AND reqhead_rec.frm = biglist.req_frm\nORDER BY prnt_item, account_amt;