DECLARE\n    v_search_value VARCHAR2(100) := '1/22/2008P09RR8';\n    v_table_name USER_TABLES.TABLE_NAME%TYPE;\n    v_column_name USER_TAB_COLUMNS.COLUMN_NAME%TYPE;\n    v_sql VARCHAR2(1000);\nBEGIN\n    -- Loop through each table in the current schema\n    FOR table_rec IN (SELECT TABLE_NAME FROM USER_TABLES) LOOP\n        v_table_name := table_rec.TABLE_NAME;\n\n        -- Loop through each column in the current table that is of a searchable type\n        FOR col_rec IN (SELECT COLUMN_NAME, DATA_TYPE \n                        FROM USER_TAB_COLUMNS \n                        WHERE TABLE_NAME = v_table_name AND DATA_TYPE IN ('VARCHAR2', 'NVARCHAR2', 'CHAR', 'NCHAR')) LOOP\n            v_column_name := col_rec.COLUMN_NAME;\n\n            -- Construct the SQL query to search for the value in the specified column\n            v_sql := 'SELECT 1 INTO tmp_result FROM ' || v_table_name \n                     || ' WHERE ' || v_column_name || ' = :search_val';\n\n            BEGIN\n                EXECUTE IMMEDIATE v_sql USING v_search_value;\n\n                -- If a row is found, output the table and column names\n                DBMS_OUTPUT.PUT_LINE('Found in: ' || v_table_name || '.' || v_column_name);\n            EXCEPTION\n                WHEN NO_DATA_FOUND THEN\n                    CONTINUE; -- Ignore no data found exceptions, continue searching\n                WHEN OTHERS THEN\n                    -- Handle unexpected errors here\n                    DBMS_OUTPUT.PUT_LINE('Error checking column: ' || v_column_name || ' in table: ' || v_table_name);\n                    DBMS_OUTPUT.PUT_LINE(SQLERRM);\n            END;\n        END LOOP;\n\n        -- Optional: Provide progress update after processing a table\n        DBMS_OUTPUT.PUT_LINE('Checked all columns for table: ' || v_table_name);\n    END LOOP;\nEND;\n/