CREATE PROCEDURE GetFormattedDates\nAS\nBEGIN\n    SELECT \n        YourDateTimeColumn,\n        FORMAT(YourDateTimeColumn, 'ddMMM', 'en-US') AS DDMMM,\n        RIGHT('0' + CONVERT(VARCHAR(2), DATEPART(hour, YourDateTimeColumn)), 2) +\n        RIGHT('0' + CONVERT(VARCHAR(2), DATEPART(minute, YourDateTimeColumn)), 2) +\n        CASE WHEN DATEPART(hour, YourDateTimeColumn) < 12 THEN 'A' ELSE 'P' END AS HHMMT\n    FROM \n        YourTable;\nEND;