CREATE TABLE Course (\n    CourseID INT PRIMARY KEY AUTO_INCREMENT,\n    DeptPrefix VARCHAR(10),\n    Number INT,\n    Title VARCHAR(255),\n    Description TEXT,\n    StartTime TIME,\n    EndTime TIME\n);\n\nCREATE TABLE DayOfWeek (\n    DayID INT PRIMARY KEY AUTO_INCREMENT,\n    DayName VARCHAR(10) UNIQUE -- e.g., 'Monday', 'Tuesday', ...\n);\n\nCREATE TABLE Course_DayOfWeek_Junction (\n    CourseID INT,\n    DayID INT,\n    PRIMARY KEY (CourseID, DayID),\n    FOREIGN KEY (CourseID) REFERENCES Course(CourseID),\n    FOREIGN KEY (DayID) REFERENCES DayOfWeek(DayID)\n);