WITH SplitCTE AS (\n    SELECT \n        value = LTRIM(RTRIM(SUBSTRING(description, 1, CHARINDEX(' ', description + ' ') - 1))),\n        remainingTxt = SUBSTRING(description, CHARINDEX(' ', description + ' ') + 1, LEN(description))\n    FROM \n        FieldRecords\n\n    UNION ALL\n\n    SELECT \n        value = LTRIM(RTRIM(SUBSTRING(remainingTxt, 1, CHARINDEX(' ', remainingTxt + ' ') - 1))),\n        remainingTxt = SUBSTRING(remainingTxt, CHARINDEX(' ', remainingTxt + ' ') + 1, LEN(remainingTxt))\n    FROM \n        SplitCTE\n    WHERE \n        remainingTxt > ''\n)\nSELECT DISTINCT value AS Word\nFROM SplitCTE\nWHERE value <> ''\nORDER BY Word;