CREATE TABLE [dbo].[IDGenerator1]\n(\n    CurrentID INT NOT NULL PRIMARY KEY CLUSTERED DEFAULT(0)\n)\n\nINSERT INTO [dbo].[IDGenerator1] VALUES (0);\n\nCREATE TABLE [dbo].[IDGenerator2]\n(\n    CurrentID INT NOT NULL PRIMARY KEY CLUSTERED DEFAULT(0)\n)\n\nINSERT INTO [dbo].[IDGenerator2] VALUES (0);\n\n\nCREATE PROCEDURE GetNextID1\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    UPDATE [dbo].[IDGenerator1]\n    SET @CurrentID = CurrentID + 1,\n        CurrentID = CurrentID + 1\n\n    RETURN @CurrentID;\nEND\n\n\nCREATE PROCEDURE GetNextID2\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @CurrentID INT;\n\n    UPDATE [dbo].[IDGenerator2]\n    SET @CurrentID = CurrentID + 1,\n        CurrentID = CurrentID + 1\n\n    RETURN @CurrentID;\nEND\n\n\n-- Example usage to insert into your tables:\nDECLARE @NewID1 INT\nEXEC @NewID1 = GetNextID1;\nINSERT INTO Table1 (id, OtherColumn) VALUES (@NewID1, 'value');\n\nDECLARE @NewID2 INT\nEXEC @NewID2 = GetNextID2;\nINSERT INTO Table2 (id, OtherColumn) VALUES (@NewID2, 'value');