CREATE TABLE tblusers (\n    UsrID INT PRIMARY KEY AUTO_INCREMENT,\n    First VARCHAR(50),\n    Last VARCHAR(50),\n    DOB DATE,\n    PhoneNumber VARCHAR(15)\n);\n\nCREATE TABLE tbluser_images (\n    ImageID INT PRIMARY KEY AUTO_INCREMENT,\n    UsrID INT,\n    FilePath VARCHAR(255) NOT NULL,  -- Store path to the image file on server\n    ThumbnailPath VARCHAR(255),     -- Store path to the thumbnail image file on server\n    IsDefault BOOLEAN DEFAULT FALSE,  -- True if this is the default profile picture\n    UploadDate TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (UsrID) REFERENCES tblusers(UsrID)\n);