-- Table for the first external status\nCREATE TABLE ext_status_1 (\n    id INT PRIMARY KEY,\n    status_description VARCHAR(255) NOT NULL\n);\n\n-- Table for the second external status\nCREATE TABLE ext_status_2 (\n    id INT PRIMARY KEY,\n    status_description VARCHAR(255) NOT NULL\n);\n\n-- Prospect table\nCREATE TABLE prospect (\n    prospect_id INT AUTO_INCREMENT PRIMARY KEY,\n    ext_status_1_id INT,\n    ext_status_2_id INT,\n    FOREIGN KEY (ext_status_1_id) REFERENCES ext_status_1(id),\n    FOREIGN KEY (ext_status_2_id) REFERENCES ext_status_2(id)\n);\n\n-- Trigger to enforce the business rule\nDELIMITER //\nCREATE TRIGGER before_prospect_insert BEFORE INSERT ON prospect FOR EACH ROW\nBEGIN\n    IF NEW.ext_status_1_id != 1 THEN\n        SET NEW.ext_status_2_id = NULL;\n    END IF;\nEND; //\n\nCREATE TRIGGER before_prospect_update BEFORE UPDATE ON prospect FOR EACH ROW\nBEGIN\n    IF NEW.ext_status_1_id != 1 THEN\n        SET NEW.ext_status_2_id = NULL;\n    END IF;\nEND; //\nDELIMITER ;