-- Step 1: Create Summary Table\nCREATE TABLE FormSummary (\n    FormID INT PRIMARY KEY,\n    Content VARCHAR(MAX)\n);\n\n-- Step 2: Create Triggers to Populate/Fill the FormSummary table\n\n-- Trigger for Inserts/Updates on Answer table\nCREATE TRIGGER trg_Answer_AfterInsertUpdate ON Answer\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    UPDATE fs\n    SET Content = a.ContentString\n    FROM FormSummary fs\n    JOIN (\n        SELECT FormID,\n               STUFF((\n                    SELECT ' ' + Text\n                    FROM Answer\n                    WHERE FormID = i.FormID\n                    FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') AS ContentString\n        FROM inserted i\n        GROUP BY FormID\n    ) a ON fs.FormID = a.FormID;\nEND;\n\n-- Trigger for Inserts/Updates on Comment table\nCREATE TRIGGER trg_Comment_AfterInsertUpdate ON Comment\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    UPDATE fs\n    SET Content += ' ' + c.ContentString -- Append to existing content\n    FROM FormSummary fs\n    JOIN (\n        SELECT FormID,\n               STUFF((\n                    SELECT ' ' + Text\n                    FROM Comment\n                    WHERE FormID = i.FormID\n                    FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') AS ContentString\n        FROM inserted i\n        GROUP BY FormID\n    ) c ON fs.FormID = c.FormID;\nEND;\n\n-- Step 3: Create FullText Catalog and Index on the summary table column\nCREATE FULLTEXT CATALOG MyFTCatalog;\nCREATE FULLTEXT INDEX ON FormSummary(Content)\nKEY INDEX PK_FormSummary -- Assuming you have a primary key index\nON MyFTCatalog;