DECLARE\n    l_sql VARCHAR2(32767);\nBEGIN\n    -- Start building the SQL query\n    l_sql := 'CREATE OR REPLACE VIEW ALL_SERV AS ';\n\n    FOR rec IN (SELECT owner FROM all_objects WHERE object_type = 'VIEW' AND object_name = 'SERV') LOOP\n        IF l_sql != 'CREATE OR REPLACE VIEW ALL_SERV AS ' THEN\n            l_sql := l_sql || ' UNION ALL ';\n        END IF;\n        l_sql := l_sql || ' SELECT * FROM ' || rec.owner || '.SERV';\n    END LOOP;\n\n    -- Execute the dynamic SQL to create the view\n    EXECUTE IMMEDIATE l_sql;\nEND;\n/