-- Change schema to dbo for each table\nEXEC sp_changeobjectowner 'jonathan.MovieData', 'dbo';\n\n-- Alternatively, use ALTER AUTHORIZATION (Recommended)\nALTER AUTHORIZATION ON SCHEMA::jonathan TO dbo;\n\n-- If you need to change the owner of all tables in one go using a script:\nDECLARE @sql NVARCHAR(MAX) = N'';\nSELECT @sql += N'\nALTER AUTHORIZATION ON OBJECT::' + QUOTENAME('jonathan') + '.' + QUOTENAME(name) + ' TO dbo;'\nFROM sys.tables\nWHERE schema_id = SCHEMA_ID('jonathan');\n\nEXEC sp_executesql @sql;