-- Creating a physical table\nCREATE TABLE PhysicalTable (\n    ID INT PRIMARY KEY,\n    Name NVARCHAR(100),\n    Data NVARCHAR(MAX)\n);\n\n-- Inserting data into the physical table\nINSERT INTO PhysicalTable (ID, Name, Data)\nVALUES (1, 'Sample', 'Some sample data');\n\n-- Querying the physical table\nSELECT * FROM PhysicalTable;\n\n-- Dropping the physical table when done\nDROP TABLE PhysicalTable;