-- Create table with an identity column\nCREATE TABLE MyTable (\n    Id INT IDENTITY(100, 1) PRIMARY KEY,\n    Name NVARCHAR(50)\n);\n\n-- Trigger to reset the identity value if it exceeds the limit\nCREATE TRIGGER trg_ResetIdentity\nON MyTable\nAFTER INSERT\nAS\nBEGIN\n    IF (IDENT_CURRENT('MyTable') > 999)\n    BEGIN\n        DBCC CHECKIDENT ('MyTable', RESEED, 100);\n        RAISERROR ('Id exceeded the maximum limit. Resetting identity to 100.', 16, 1);\n        ROLLBACK TRANSACTION;\n    END;\nEND;