-- Begin transaction\nBEGIN;\n\n-- Create a new table with the desired column order\nCREATE TABLE person_new (\n    id SERIAL PRIMARY KEY,\n    first_name VARCHAR(50),\n    last_name VARCHAR(50),\n    dob DATE,\n    -- Add other columns in the desired order here\n);\n\n-- Copy data from old to new table\nINSERT INTO person_new (id, first_name, last_name, dob /*, other columns */)\nSELECT id, first_name, last_name, dob /*, other columns */\nFROM person;\n\n-- Drop the old table\nDROP TABLE person;\n\n-- Rename the new table to the original name\nALTER TABLE person_new RENAME TO person;\n\n-- Commit transaction\nCOMMIT;