-- Create new schemas\nCREATE SCHEMA Application AUTHORIZATION dbo;\nCREATE SCHEMA Security AUTHORIZATION dbo;\n\n-- Create a table within each schema\nCREATE TABLE Application.Users (\n    UserID INT PRIMARY KEY,\n    Username NVARCHAR(50)\n);\n\nCREATE TABLE Security.Logs (\n    LogID INT PRIMARY KEY,\n    Message NVARCHAR(255)\n);\n\n-- Create roles and assign permissions on schemas\nCREATE ROLE AppUserRole;\nCREATE ROLE SecurityAdminRole;\n\nGRANT SELECT, INSERT ON SCHEMA::Application TO AppUserRole;\nGRANT SELECT, INSERT, UPDATE, DELETE ON SCHEMA::Security TO SecurityAdminRole;