CREATE PROCEDURE GetNewsWithIncrementalDays (@InitialDays INT)\nAS\nBEGIN\n    DECLARE @days INT = @InitialDays\n\n    WHILE 1=1\n    BEGIN\n        SELECT * \n        FROM news\n        WHERE DATEDIFF(day, date, GETDATE()) <= @days\n        ORDER BY date DESC\n\n        IF @@ROWCOUNT > 0\n            BREAK -- Exit the loop if rows are found\n            \n        SET @days = @days + 1\n    END\nEND