CREATE TABLE IF NOT EXISTS `search_terms` (\n    `id` INT AUTO_INCREMENT PRIMARY KEY,\n    `term` VARCHAR(255) NOT NULL,\n    `user_id` INT DEFAULT NULL,  -- Store user ID if available\n    `ip_address` VARCHAR(45) DEFAULT NULL,  -- Store IP address for anonymous searches\n    `search_time` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,\n    INDEX (`term`),\n    INDEX (`search_time`)\n);