DECLARE @var VARCHAR(10) = 'xyz'; -- Set your variable here\n\nSELECT \n    CASE \n        WHEN @var = 'xyz' THEN col1\n        ELSE NULL -- or another default value/column\n    END AS ColumnX,\n    CASE \n        WHEN @var = 'xyz' OR @var = 'zyx' THEN col2\n        ELSE NULL -- or another default value/column\n    END AS ColumnY,\n    CASE \n        WHEN @var = 'zyx' THEN col3\n        ELSE NULL -- or another default value/column\n    END AS ColumnZ,\n    CASE \n        WHEN @var NOT IN ('xyz', 'zyx') THEN col7\n        ELSE NULL -- or another default value/column\n    END AS ColumnA,\n    CASE \n        WHEN @var NOT IN ('xyz', 'zyx') THEN col8\n        ELSE NULL -- or another default value/column\n    END AS ColumnB\nFROM your_table_name;