SELECT \n    d.location, \n    d.id \nFROM \n    closing_balance..cb_deposits AS d\nWHERE \n    (SELECT SUM(c.check_amt) \n     FROM closing_balance..cb_checkbatchhf AS b \n     INNER JOIN closing_balance..cb_checkdf AS c ON b.id = c.batch_id AND b.loc = c.loc\n     WHERE d.id = b.deposit_id AND d.location = b.loc) = d.amount\n    AND\n    (SELECT SUM(b.scanned_subtotal) \n     FROM closing_balance..cb_checkbatchhf AS b \n     WHERE d.id = b.deposit_id AND d.location = b.loc) = d.amount\n    AND\n    (SELECT SUM(b.num_checks_scanned) \n     FROM closing_balance..cb_checkbatchhf AS b \n     WHERE d.id = b.deposit_id AND d.location = b.loc) = d.count;