-- Create lookup table\nCREATE TABLE Status (\n    StatusID INT PRIMARY KEY,\n    StatusDescription VARCHAR(255) NOT NULL UNIQUE\n);\n\n-- Insert some statuses into the lookup table\nINSERT INTO Status (StatusID, StatusDescription)\nVALUES (1, 'Active'), (2, 'Inactive');\n\n-- Create another table referencing the lookup table with foreign keys\nCREATE TABLE Users (\n    UserID INT PRIMARY KEY,\n    UserName VARCHAR(255) NOT NULL,\n    UserStatus INT,\n    FOREIGN KEY (UserStatus) REFERENCES Status(StatusID)\n);\n\n-- Insert a user referencing a status via its ID\nINSERT INTO Users (UserID, UserName, UserStatus)\nVALUES (1, 'JohnDoe', 1);