-- Using ON clause which can handle such cases more explicitly\nSELECT left_table.id AS left_id, right_table.id AS right_id, ...\nFROM annoyingly_long_left_hand_table AS left_table\nLEFT JOIN annoyingly_long_right_hand_table AS right_table\nON left_table.id = right_table.id;\n\n-- Using USING, only one 'id' will be in the result set\nSELECT * \nFROM annoyingly_long_left_hand_table\nLEFT JOIN annoyingly_long_right_hand_table\nUSING (id);