DELIMITER $$\n\nCREATE PROCEDURE UpsertRecord(IN pid INT, IN pfield VARCHAR(255))\nBEGIN\n    DECLARE rowCount INT;\n    \n    -- Check if the record exists\n    SELECT COUNT(*) INTO rowCount FROM your_table_name WHERE critical_id = pid LIMIT 1;\n    \n    IF rowCount > 0 THEN\n        -- Update the record if it exists\n        UPDATE your_table_name SET field = pfield WHERE critical_id = pid;\n    ELSE\n        -- Insert a new record if it does not exist\n        INSERT INTO your_table_name (critical_id, field) VALUES (pid, pfield);\n    END IF;\nEND$$\n\nDELIMITER ;