-- Create table with spatial index\nCREATE TABLE users (\n    id INT PRIMARY KEY,\n    location POINT SRID 4326, -- Use WGS84 for latitude/longitude\n    SPATIAL INDEX(location)\n);\n\n-- Insert latitude and longitude as a point\nINSERT INTO users (id, location) VALUES (1, ST_GeomFromText('POINT(longitude latitude)', 4326));\n\n-- Query users within radius\nSELECT id\nFROM users\nWHERE MBRWithin(location, ST_Buffer(ST_SetSRID(ST_MakePoint(?, ?), 4326), ?)) = 1;