-- V1__Init.sql - Initial structure\nCREATE TABLE users (\n    id INT PRIMARY KEY,\n    username VARCHAR(255) NOT NULL\n);\n\n-- V2__Add_Password_Column.sql - Adding password column to the user table\nALTER TABLE users ADD COLUMN password VARCHAR(255) NOT NULL;\n\n-- V3__Normalize_UserDetails_Table.sql - Normalization step\nCREATE TABLE userDetails (\n    id INT PRIMARY KEY,\n    user_id INT NOT NULL,\n    details TEXT,\n    FOREIGN KEY (user_id) REFERENCES users(id)\n);\n\n-- V4__Denormalize_UserDetails_Table.sql - Denormalization step if necessary\nALTER TABLE users ADD COLUMN details TEXT;\nUPDATE users SET details = (SELECT details FROM userDetails WHERE userDetails.user_id = users.id);\nDROP TABLE userDetails;